insert  into `ghichu`(`id`,`pham`,`cau`,`thutu`,`noidung`) values (1,1,9,1,'Áo màu vàng, chỉ bậc xuất gia.'),(2,2,30,1,'Ðế Thích: Magha (Manavaka), vị lãnh đạo chư Thiên'),(3,3,37,1,'Trú xứ của Thức'),(4,3,37,2,'Dục giới, sắc giới, vô sắc giới'),(5,3,40,3,'Tức là quán (vipassana) mới chứng được.'),(6,3,40,4,'Ðối với các thiền mới chứng'),(7,3,42,5,'Hướng về 10 pháp bất thiện như sát sanh v.v...'),(8,20,283,1,'Dục vọng'),(9,21,294,1,'Ái dục và mạn.'),(10,21,294,2,'Thường kiến và biên kiến.'),(11,21,294,3,'12 xứ'),(12,21,295,4,'Nghi'),(13,22,311,1,'Cỏ Kusa'),(14,24,339,1,'Tham ái có 3 loại: dục lạc, chấp hữu, chấp phi hữu; kết hợp với 6 căn và 6 trần thành 36 dòng ái'),(15,24,344,2,'Lìa ái dục gia đình, lại chạy theo khổ hạnh ở rừng núi'),(16,25,370,1,'Thân kiến, nghi, giới cấm thủ, dục ái, sân.'),(17,25,370,2,'Sắc ái, vô sắc ái, mạn, trạo cử, vô minh.'),(18,25,370,3,'Tín, tấn, niệm, định, tuệ.'),(19,25,370,4,'5 trói buộc: tham, sân, si, mạn, tà kiến'),(20,25,370,5,'Bộc lưu: dòng nước lũ'),(21,25,377,6,'Bông lài.'),(22,26,384,1,'Thiền Chỉ và Thiền Quán'),(23,26,385,2,'6 nội xứ, 6 ngoại xứ'),(24,26,390,3,'Sự trả đũa, trả thù'),(25,26,389,4,'Hận.'),(26,26,398,5,'Ái.'),(27,26,398,6,'Tà kiến.'),(28,26,398,7,'Tuỳ miên.'),(29,26,398,8,'Vô minh.');

